import React, { useEffect, useState } from 'react';
import { db } from '../firebase';
import { collection, doc, onSnapshot, updateDoc } from 'firebase/firestore';
import { useNavigate, useLocation } from 'react-router-dom';

function Lobby() {
  const navigate = useNavigate();
  const location = useLocation();
  const { gameCode, pseudo, isHost } = location.state || {};
  const [joueurs, setJoueurs] = useState([]);

  useEffect(() => {
    if (!gameCode) return;

    const unsub = onSnapshot(collection(db, "games", gameCode, "players"), (snap) => {
      const list = snap.docs.map(doc => doc.data());
      setJoueurs(list);
    });

    return () => unsub();
  }, [gameCode]);

  const handleStart = async () => {
    await updateDoc(doc(db, "games", gameCode), {
      started: true
    });
    navigate('/game', { state: { gameCode, pseudo } });
  };

  return (
    <div style={{ padding: 20 }}>
      <h2>üîó Code de partie : {gameCode}</h2>
      <p>üëë Host : {isHost ? pseudo : ''}</p>
      <h3>üß† Joueurs dans la partie :</h3>
      <ul>
        {joueurs.map((j, i) => (
          <li key={i}>{j.pseudo}</li>
        ))}
      </ul>
      <p><i>Pour jouer, soyez en vocal avec vos amis (Discord, Facetime‚Ä¶) ou en pr√©sentiel.</i></p>

      {isHost && (
        <button onClick={handleStart} style={{ marginTop: 20 }}>
          ‚ñ∂Ô∏è Lancer la partie
        </button>
      )}
    </div>
  );
}

export default Lobby;
