// src/components/Home.js

import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

function Home({ setPseudo, setGameCode }) {
  const [name, setName] = useState('');
  const [code, setCode] = useState('');
  const [mode, setMode] = useState(null);
  const navigate = useNavigate();

  const handleStart = () => {
    if (!name.trim()) return alert("Entrez un pseudo");

    setPseudo(name.trim());

    if (mode === 'join') {
      if (!code.trim()) return alert("Entrez un code");
      setGameCode(code.trim().toUpperCase());
      navigate('/lobby');
    } else {
      const generated = generateCode();
      setGameCode(generated);
      navigate('/lobby');
    }
  };

  const generateCode = () => {
    const letters = 'ABCDEFGHIJKLMNOPQRSTUVWXYZ';
    const nums = '0123456789';
    let result = '';
    for (let i = 0; i < 2; i++) result += letters[Math.floor(Math.random() * letters.length)];
    for (let i = 0; i < 2; i++) result += nums[Math.floor(Math.random() * nums.length)];
    return result;
  };

  return (
    <div style={{ padding: 30 }}>
      <h1>ðŸŽ´ Crack List Web</h1>

      <input
        type="text"
        placeholder="Ton pseudo"
        value={name}
        onChange={(e) => setName(e.target.value)}
        style={{ padding: 10, fontSize: 16 }}
      />

      <div style={{ marginTop: 20 }}>
        <button onClick={() => setMode('create')} style={{ marginRight: 10 }}>
          CrÃ©er une partie
        </button>
        <button onClick={() => setMode('join')}>
          Rejoindre une partie
        </button>
      </div>

      {mode === 'join' && (
        <div style={{ marginTop: 15 }}>
          <input
            type="text"
            placeholder="Code de partie"
            value={code}
            onChange={(e) => setCode(e.target.value.toUpperCase())}
            style={{ padding: 10, fontSize: 16 }}
          />
        </div>
      )}

      {mode && (
        <div style={{ marginTop: 20 }}>
          <button onClick={handleStart} style={{ fontSize: 18, padding: '10px 20px' }}>
            Entrer dans la partie
          </button>
        </div>
      )}
    </div>
  );
}

export default Home;
