// src/App.js

import React, { useState } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import Home from './components/Home';
import Lobby from './components/Lobby';
import Game from './components/Game';

function App() {
  const [pseudo, setPseudo] = useState('');
  const [gameCode, setGameCode] = useState('');

  if (!pseudo) {
    return (
      <Router>
        <Routes>
          <Route path="*" element={<Home setPseudo={setPseudo} setGameCode={setGameCode} />} />
        </Routes>
      </Router>
    );
  }

  return (
    <Router>
      <Routes>
        <Route path="/" element={<Navigate to="/lobby" />} />
        <Route path="/lobby" element={<Lobby pseudo={pseudo} gameCode={gameCode} setGameCode={setGameCode} />} />
        <Route path="/game/:code" element={<Game pseudo={pseudo} />} />
      </Routes>
    </Router>
  );
}

export default App;
