// src/App.js
import React, { useState, useEffect } from 'react';
import { db } from './firebase';
import { doc, setDoc, getDoc, onSnapshot, collection, addDoc } from 'firebase/firestore';
import Lobby from './components/Lobby';
import Game from './components/Game';

function App() {
  const [pseudo, setPseudo] = useState('');
  const [gameCode, setGameCode] = useState('');
  const [isHost, setIsHost] = useState(false);
  const [joined, setJoined] = useState(false);
  const [started, setStarted] = useState(false);
  const [mode, setMode] = useState(null);

  const generateCode = () => {
    const letters = 'ABCDEFGHIJKLMNOPQRSTUVWXYZ';
    const nums = '0123456789';
    let result = '';
    for (let i = 0; i < 2; i++) result += letters[Math.floor(Math.random() * letters.length)];
    for (let i = 0; i < 2; i++) result += nums[Math.floor(Math.random() * nums.length)];
    return result;
  };

  const handleCreate = async () => {
    if (!pseudo.trim()) return alert("Entre un pseudo");

    const code = generateCode();

    // CrÃ©er la partie
    await setDoc(doc(db, "games", code), {
      createdAt: Date.now(),
      started: false,
      host: pseudo
    });

    // Ajouter lâ€™hÃ´te comme premier joueur
    await addDoc(collection(db, "games", code, "players"), {
      pseudo,
      out: false
    });

    setGameCode(code);
    setIsHost(true);
    setJoined(true);
  };

  const handleJoin = async () => {
    if (!pseudo.trim() || !gameCode.trim()) return alert("Pseudo ou code manquant");

    const ref = doc(db, "games", gameCode);
    const snap = await getDoc(ref);

    if (!snap.exists()) {
      alert("Aucune partie trouvÃ©e avec ce code.");
      return;
    }

    // Ajouter le joueur
    await addDoc(collection(db, "games", gameCode, "players"), {
      pseudo,
      out: false
    });

    setIsHost(false);
    setJoined(true);
  };

  useEffect(() => {
    if (joined && gameCode) {
      const unsub = onSnapshot(doc(db, "games", gameCode), (docSnap) => {
        if (docSnap.exists()) {
          const data = docSnap.data();
          setStarted(data.started);
        }
      });
      return () => unsub();
    }
  }, [joined, gameCode]);

  if (started) {
    return <Game gameCode={gameCode} pseudo={pseudo} />;
  }

  if (joined) {
    return <Lobby gameCode={gameCode} pseudo={pseudo} isHost={isHost} />;
  }

  return (
    <div style={{ padding: 20 }}>
      <h1>ðŸŽ´ Crack List Web</h1>

      <input
        type="text"
        placeholder="Ton pseudo"
        value={pseudo}
        onChange={(e) => setPseudo(e.target.value)}
      />

      <div style={{ marginTop: 15 }}>
        <button onClick={() => setMode('create')}>CrÃ©er une partie</button>
        <button onClick={() => setMode('join')}>Rejoindre une partie</button>
      </div>

      {mode === 'join' && (
        <>
          <div style={{ marginTop: 10 }}>
            <input
              type="text"
              placeholder="Code de partie"
              value={gameCode}
              onChange={(e) => setGameCode(e.target.value.toUpperCase())}
            />
          </div>
          <div style={{ marginTop: 10 }}>
            <button onClick={handleJoin}>Rejoindre</button>
          </div>
        </>
      )}

      {mode === 'create' && (
        <div style={{ marginTop: 20 }}>
          <button onClick={handleCreate}>CrÃ©er et rejoindre</button>
        </div>
      )}
    </div>
  );
}

export default App;
