import React, { useEffect, useState } from 'react';
import { db } from '../firebase';
import {
  doc,
  getDoc,
  onSnapshot,
  updateDoc,
  arrayRemove,
  arrayUnion
} from 'firebase/firestore';

function Game({ gameCode, pseudo }) {
  const [cartes, setCartes] = useState([]);
  const [joueurActif, setJoueurActif] = useState(null);
  const [piocheRestante, setPiocheRestante] = useState(0);
  const [timer, setTimer] = useState(15);
  const [monTour, setMonTour] = useState(false);

  useEffect(() => {
    const playerRef = doc(db, "games", gameCode, "players", pseudo);
    const unsubPlayer = onSnapshot(playerRef, (docSnap) => {
      if (docSnap.exists()) {
        const data = docSnap.data();
        if (data.cartes) setCartes(data.cartes);
      }
    });

    const gameRef = doc(db, "games", gameCode);
    const unsubGame = onSnapshot(gameRef, (docSnap) => {
      if (docSnap.exists()) {
        const data = docSnap.data();
        setJoueurActif(data.currentPlayer);
        setPiocheRestante(data.pioche?.length || 0);
        setMonTour(data.currentPlayer === pseudo);
        setTimer(15);
      }
    });

    return () => {
      unsubPlayer();
      unsubGame();
    };
  }, [gameCode, pseudo]);

  useEffect(() => {
    if (!monTour) return;
    if (timer === 0) {
      handleTimeout();
      return;
    }
    const interval = setInterval(() => setTimer((prev) => prev - 1), 1000);
    return () => clearInterval(interval);
  }, [monTour, timer]);

  const handleTimeout = async () => {
    const gameRef = doc(db, "games", gameCode);
    const snap = await getDoc(gameRef);
    const gameData = snap.data();

    const pioche = gameData.pioche || [];
    const nextCard = pioche[0];
    const newPioche = pioche.slice(1);

    await updateDoc(doc(db, "games", gameCode, "players", pseudo), {
      cartes: arrayUnion(nextCard)
    });

    await updateDoc(gameRef, {
      pioche: newPioche,
      currentPlayer: getNextPlayer(gameData, pseudo)
    });
  };

  const getNextPlayer = (gameData, current) => {
    const sens = gameData.sens || 1;
    const players = Object.keys(gameData.players || {});
    const index = players.indexOf(current);
    const nextIndex = (index + sens + players.length) % players.length;
    return players[nextIndex];
  };

  const handlePlayCard = async (card) => {
    const gameRef = doc(db, "games", gameCode);
    const snap = await getDoc(gameRef);
    const gameData = snap.data();

    await updateDoc(doc(db, "games", gameCode, "players", pseudo), {
      cartes: arrayRemove(card)
    });

    await updateDoc(gameRef, {
      currentPlayer: getNextPlayer(gameData, pseudo)
    });
  };

  return (
    <div style={{ padding: 20 }}>
      <h2>üéÆ Partie : {gameCode}</h2>
      <p>Tu es : <strong>{pseudo}</strong></p>
      <p>Joueur actif : <strong>{joueurActif}</strong></p>
      <p>Mon tour : {monTour ? "‚úÖ OUI" : "‚ùå NON"}</p>
      {monTour && <p>‚è≥ Temps restant : {timer} s</p>}

      <h3>üÉè Tes cartes :</h3>
      <div style={{ display: 'flex', flexWrap: 'wrap' }}>
        {cartes.map((carte, i) => (
          <button
            key={i}
            onClick={() => monTour && handlePlayCard(carte)}
            style={{
              margin: 5,
              padding: 10,
              backgroundColor: monTour ? '#d0f0c0' : '#eee',
              cursor: monTour ? 'pointer' : 'default'
            }}
          >
            {carte}
          </button>
        ))}
      </div>
    </div>
  );
}

export default Game;
