// src/components/VotePanel.js

import React, { useEffect, useState } from 'react';

function VotePanel({ pseudoJoueurActif, onVoteResult, isMyTurn }) {
  const [vote, setVote] = useState(null);
  const [timer, setTimer] = useState(5);

  useEffect(() => {
    const interval = setInterval(() => {
      setTimer(t => {
        if (t <= 1) {
          clearInterval(interval);
          terminerVote();
        }
        return t - 1;
      });
    }, 1000);

    return () => clearInterval(interval);
  }, []);

  const terminerVote = () => {
    // Pour l‚Äôinstant simple : 1 seul joueur = son vote d√©cide
    if (vote === 'valider') {
      onVoteResult('valid√©');
    } else {
      onVoteResult('refus√©');
    }
  };

  if (isMyTurn) {
    return <p>üó≥Ô∏è En attente des votes des autres joueurs...</p>;
  }

  return (
    <div style={{ marginTop: 30 }}>
      <p>üó≥Ô∏è Validez-vous la r√©ponse de <strong>{pseudoJoueurActif}</strong> ?</p>
      <p>Temps restant : {timer}s</p>
      <button onClick={() => setVote('valider')} disabled={vote}>‚úÖ Valider</button>
      <button onClick={() => setVote('refuser')} disabled={vote} style={{ marginLeft: 10 }}>‚ùå Refuser</button>
    </div>
  );
}

export default VotePanel;
