import React, { useState, useEffect } from 'react';
import { doc, updateDoc } from 'firebase/firestore';
import { db } from '../firebase';
import CurrentTheme from './CurrentTheme';
import Timer from './Timer';
import WinnerPanel from './WinnerPanel';

const LETTER_PENALTIES = {
  'A': 0, 'B': 0, 'C': 0, 'D': 0, 'F': 0, 'G': 0, 'L': 0, 'M': 0, 'N': 0, 'P': 0, 'R': 0, 'S': 0, 'T': 0, 'V': 0,
  'E': 1, 'I': 1, 'J': 1, 'O': 1,
  'H': 2, 'K': 2, 'U': 2, 'Y': 2,
  'Q': 3, 'W': 3, 'X': 3, 'Z': 3
};

function Game({ roomCode, playerName, playerId, roomData, onLeaveRoom }) {
  const [selectedCard, setSelectedCard] = useState(null);
  const [userWord, setUserWord] = useState('');
  const [jokerLetter, setJokerLetter] = useState('');
  const [swapTarget, setSwapTarget] = useState('');
  const [swapMode, setSwapMode] = useState('single'); // 'single' ou 'all'
  const [vote, setVote] = useState(null);
  const [loading, setLoading] = useState(false);

  const currentPlayer = roomData?.players && Array.isArray(roomData.players) 
    ? roomData.players[roomData.currentPlayerIndex] 
    : null;
  const isMyTurn = currentPlayer?.id === playerId;
  const myPlayer = roomData?.players && Array.isArray(roomData.players) 
    ? roomData.players.find(p => p.id === playerId) 
    : null;
  const gamePhase = roomData?.gamePhase || 'theme-selection';

  useEffect(() => {
    // Gérer les déconnexions
    const handleDisconnection = async () => {
      if (roomData && myPlayer && roomData.players && Array.isArray(roomData.players)) {
        const playerIndex = roomData.players.findIndex(p => p.id === playerId);
        if (playerIndex !== -1) {
          await updateDoc(doc(db, 'rooms', roomCode), {
            [`players.${playerIndex}.lastSeen`]: Date.now(),
            [`players.${playerIndex}.connected`]: true
          });
        }
      }
    };

    handleDisconnection();
    const interval = setInterval(handleDisconnection, 5000);

    return () => clearInterval(interval);
  }, [roomCode, playerId, roomData, myPlayer]);

  // Vérifier s'il y a un gagnant
  const playersWithCards = roomData?.players && Array.isArray(roomData.players) 
    ? roomData.players.filter(p => p.cards && p.cards.length > 0) 
    : [];
  const winner = playersWithCards.length === 1 ? playersWithCards[0] : null;

  const selectTheme = async (theme) => {
    setLoading(true);
    try {
      await updateDoc(doc(db, 'rooms', roomCode), {
        currentTheme: theme,
        gamePhase: 'playing',
        themeSelectionPhase: false,
        currentTurnStartTime: Date.now()
      });
    } catch (err) {
      console.error('Erreur lors de la sélection du thème:', err);
    }
    setLoading(false);
  };

  const playCard = async () => {
    if (!selectedCard || loading) return;

    setLoading(true);
    try {
      if (!roomData?.players || !Array.isArray(roomData.players) || !myPlayer?.cards) {
        setLoading(false);
        return;
      }

      const myPlayerIndex = roomData.players.findIndex(p => p.id === playerId);
      const myCards = [...myPlayer.cards];
      const cardIndex = myCards.findIndex(c => c.id === selectedCard.id);
      
      if (cardIndex === -1) return;

      let updates = {};
      let nextPhase = 'voting';

      // Retirer la carte jouée
      myCards.splice(cardIndex, 1);
      updates[`players.${myPlayerIndex}.cards`] = myCards;

      // Traiter selon le type de carte
      if (selectedCard.type === 'letter' || selectedCard.type === 'joker') {
        if (!userWord.trim()) {
          setLoading(false);
          return;
        }

        updates.currentPlay = {
          playerId: playerId,
          playerName: playerName,
          card: selectedCard,
          word: userWord.trim(),
          jokerLetter: selectedCard.type === 'joker' ? jokerLetter : null,
          timestamp: Date.now()
        };
        updates.gamePhase = 'voting';
        updates.votingStartTime = Date.now();
        updates.votes = {};

      } else if (selectedCard.type === 'reverse') {
        updates.direction = roomData.direction * -1;
        nextPhase = 'playing';
        
      } else if (selectedCard.type === 'stop') {
        // Passer le prochain joueur
        const nextIndex = getNextPlayerIndex(roomData.currentPlayerIndex, roomData.direction, roomData.players.length);
        const skipIndex = getNextPlayerIndex(nextIndex, roomData.direction, roomData.players.length);
        updates.currentPlayerIndex = skipIndex;
        updates.currentTurnStartTime = Date.now();
        nextPhase = 'playing';
        
      } else if (selectedCard.type === 'swap') {
        if (!swapTarget) {
          setLoading(false);
          return;
        }
        
        const targetPlayerIndex = roomData.players.findIndex(p => p.id === swapTarget);
        if (targetPlayerIndex === -1) {
          setLoading(false);
          return;
        }

        const targetCards = [...roomData.players[targetPlayerIndex].cards];
        
        if (swapMode === 'single') {
          // Échanger 1 carte aléatoire
          if (myCards.length > 0 && targetCards.length > 0) {
            const myRandomIndex = Math.floor(Math.random() * myCards.length);
            const targetRandomIndex = Math.floor(Math.random() * targetCards.length);
            
            const tempCard = myCards[myRandomIndex];
            myCards[myRandomIndex] = targetCards[targetRandomIndex];
            targetCards[targetRandomIndex] = tempCard;
          }
        } else {
          // Échanger tous les paquets
          updates[`players.${myPlayerIndex}.cards`] = targetCards;
          updates[`players.${targetPlayerIndex}.cards`] = myCards;
        }
        
        if (swapMode === 'single') {
          updates[`players.${myPlayerIndex}.cards`] = myCards;
          updates[`players.${targetPlayerIndex}.cards`] = targetCards;
        }
        
        nextPhase = 'playing';
        
      } else if (selectedCard.type === 'crakitoko') {
        updates.themeChoices = selectedCard.themes;
        updates.gamePhase = 'theme-selection';
        updates.themeSelectionPhase = true;
      }

      // Passer au joueur suivant si ce n'est pas un vote
      if (nextPhase === 'playing') {
        const nextPlayerIndex = getNextPlayerIndex(roomData.currentPlayerIndex, roomData.direction, roomData.players.length);
        updates.currentPlayerIndex = nextPlayerIndex;
        updates.currentTurnStartTime = Date.now();
      }

      updates.gamePhase = nextPhase;
      await updateDoc(doc(db, 'rooms', roomCode), updates);

      // Reset des états locaux
      setSelectedCard(null);
      setUserWord('');
      setJokerLetter('');
      setSwapTarget('');
      setSwapMode('single');

    } catch (err) {
      console.error('Erreur lors du jeu de la carte:', err);
    }
    setLoading(false);
  };

  const submitVote = async (voteValue) => {
    if (loading || !roomData.currentPlay) return;

    setLoading(true);
    try {
      const votes = { ...roomData.votes };
      votes[playerId] = voteValue;

      await updateDoc(doc(db, 'rooms', roomCode), {
        votes: votes
      });

      setVote(voteValue);
    } catch (err) {
      console.error('Erreur lors du vote:', err);
    }
    setLoading(false);
  };

  const processVoteResults = async () => {
    if (!roomData.currentPlay || !roomData.votes) return;

    const votes = Object.values(roomData.votes);
    const approvals = votes.filter(v => v === true).length;
    const rejections = votes.filter(v => v === false).length;
    const isApproved = approvals >= rejections;

    const playedPlayerIndex = roomData.players.findIndex(p => p.id === roomData.currentPlay.playerId);
    let updates = {};

    if (!isApproved) {
      // Réponse refusée - remettre la carte + 1 de la pioche
      const playerCards = [...roomData.players[playedPlayerIndex].cards];
      playerCards.push(roomData.currentPlay.card);
      
      // Ajouter une carte de la pioche
      if (roomData.deck && roomData.deck.length > 0) {
        const newCard = roomData.deck[0];
        const remainingDeck = roomData.deck.slice(1);
        playerCards.push(newCard);
        updates.deck = remainingDeck;
      }
      
      updates[`players.${playedPlayerIndex}.cards`] = playerCards;
    } else {
      // Réponse acceptée - appliquer les malus si nécessaire
      const currentPlay = roomData.currentPlay;
      let penalty = 0;
      
      if (currentPlay.card.type === 'letter') {
        penalty = LETTER_PENALTIES[currentPlay.card.letter] || 0;
      } else if (currentPlay.card.type === 'joker' && currentPlay.jokerLetter) {
        penalty = LETTER_PENALTIES[currentPlay.jokerLetter] || 0;
      }
      
      // Appliquer le malus au joueur suivant
      if (penalty > 0) {
        const nextPlayerIndex = getNextPlayerIndex(roomData.currentPlayerIndex, roomData.direction, roomData.players.length);
        const nextPlayerCards = [...roomData.players[nextPlayerIndex].cards];
        
        for (let i = 0; i < penalty && roomData.deck && roomData.deck.length > 0; i++) {
          nextPlayerCards.push(roomData.deck[i]);
        }
        
        updates[`players.${nextPlayerIndex}.cards`] = nextPlayerCards;
        updates.deck = roomData.deck.slice(penalty);
      }
    }

    // Passer au joueur suivant
    const nextPlayerIndex = getNextPlayerIndex(roomData.currentPlayerIndex, roomData.direction, roomData.players.length);
    
    updates.currentPlayerIndex = nextPlayerIndex;
    updates.gamePhase = 'playing';
    updates.currentPlay = null;
    updates.votes = {};
    updates.votingStartTime = null;
    updates.currentTurnStartTime = Date.now();

    await updateDoc(doc(db, 'rooms', roomCode), updates);
  };

  const handleTimeout = async (phase) => {
    if (phase === 'playing' && isMyTurn) {
      // Timeout pendant le tour du joueur - ajouter une carte
      const myPlayerIndex = roomData.players.findIndex(p => p.id === playerId);
      const myCards = [...myPlayer.cards];
      
      if (roomData.deck && roomData.deck.length > 0) {
        myCards.push(roomData.deck[0]);
        const remainingDeck = roomData.deck.slice(1);
        
        const nextPlayerIndex = getNextPlayerIndex(roomData.currentPlayerIndex, roomData.direction, roomData.players.length);
        
        await updateDoc(doc(db, 'rooms', roomCode), {
          [`players.${myPlayerIndex}.cards`]: myCards,
          deck: remainingDeck,
          currentPlayerIndex: nextPlayerIndex,
          currentTurnStartTime: Date.now()
        });
      }
    } else if (phase === 'voting') {
      // Timeout du vote - traiter les résultats
      await processVoteResults();
    }
  };

  if (winner) {
    return <WinnerPanel winner={winner} onLeaveRoom={onLeaveRoom} />;
  }

  if (!roomData || !myPlayer) {
    return <div className="loading">Chargement...</div>;
  }

  return (
    <div className="game">
      <div className="game-header">
        <div className="room-info">
          <span className="room-code">Salle: {roomCode}</span>
          <button onClick={onLeaveRoom} className="btn btn-danger leave-btn">
            Quitter
          </button>
        </div>
        
        <CurrentTheme theme={roomData.currentTheme} />
        
        <div className="players-status">
          {roomData.players && Array.isArray(roomData.players) ? roomData.players.map((player, index) => (
            <div 
              key={player.id} 
              className={`player-status ${index === roomData.currentPlayerIndex ? 'current-player' : ''}`}
            >
              <span className="player-name">{player.name}</span>
              <span className="cards-count">{player.cards?.length || 0}</span>
            </div>
          )) : null}
        </div>
      </div>

      {/* Phase de sélection de thème */}
      {gamePhase === 'theme-selection' && isMyTurn && (
        <div className="theme-selection">
          <h2>🎯 Choisissez un thème</h2>
          <div className="theme-choices">
            {roomData.themeChoices?.map((theme, index) => (
              <button
                key={index}
                onClick={() => selectTheme(theme)}
                className="btn btn-theme"
                disabled={loading}
              >
                {theme}
              </button>
            ))}
          </div>
        </div>
      )}

      {gamePhase === 'theme-selection' && !isMyTurn && (
        <div className="waiting-theme">
          <p>⏳ {currentPlayer?.name} choisit le thème...</p>
        </div>
      )}

      {/* Phase de jeu */}
      {gamePhase === 'playing' && (
        <div className="playing-phase">
          <Timer
            duration={15}
            onTimeout={() => handleTimeout('playing')}
            startTime={roomData.currentTurnStartTime}
            active={isMyTurn}
          />

          {isMyTurn ? (
            <div className="my-turn">
              <h2>🎮 À votre tour !</h2>
              
              <div className="my-cards">
                <h3>Vos cartes:</h3>
                <div className="cards-grid">
                  {myPlayer?.cards && Array.isArray(myPlayer.cards) ? myPlayer.cards.map((card) => (
                    <div
                      key={card.id}
                      className={`card ${selectedCard?.id === card.id ? 'selected' : ''}`}
                      onClick={() => setSelectedCard(card)}
                    >
                      {card.type === 'letter' && (
                        <div className="letter-card">
                          <span className="letter">{card.letter}</span>
                          <span className="penalty">+{LETTER_PENALTIES[card.letter]}</span>
                        </div>
                      )}
                      {card.type === 'joker' && (
                        <div className="joker-card">🃏 JOKER</div>
                      )}
                      {card.type === 'reverse' && (
                        <div className="special-card">🔄 SENS</div>
                      )}
                      {card.type === 'swap' && (
                        <div className="special-card">🔄 ÉCHANGE</div>
                      )}
                      {card.type === 'stop' && (
                        <div className="special-card">⏸️ STOP</div>
                      )}
                      {card.type === 'crakitoko' && (
                        <div className="crakitoko-card">🎯 THÈME</div>
                      )}
                    </div>
                  )) : (
                    <div>Aucune carte disponible</div>
                  )}
                </div>
              </div>

              {selectedCard && (
                <div className="card-action">
                  {(selectedCard.type === 'letter' || selectedCard.type === 'joker') && (
                    <div className="word-input">
                      <input
                        type="text"
                        placeholder="Votre mot..."
                        value={userWord}
                        onChange={(e) => setUserWord(e.target.value)}
                        className="input-field"
                      />
                      
                      {selectedCard.type === 'joker' && (
                        <select
                          value={jokerLetter}
                          onChange={(e) => setJokerLetter(e.target.value)}
                          className="select-field"
                        >
                          <option value="">Choisir une lettre</option>
                          {Object.keys(LETTER_PENALTIES).map(letter => (
                            <option key={letter} value={letter}>
                              {letter} (+{LETTER_PENALTIES[letter]})
                            </option>
                          ))}
                        </select>
                      )}
                    </div>
                  )}

                  {selectedCard.type === 'swap' && (
                    <div className="swap-controls">
                      <select
                        value={swapTarget}
                        onChange={(e) => setSwapTarget(e.target.value)}
                        className="select-field"
                      >
                        <option value="">Choisir un joueur</option>
                        {roomData.players && Array.isArray(roomData.players) 
                          ? roomData.players
                            .filter(p => p.id !== playerId)
                            .map(player => (
                              <option key={player.id} value={player.id}>
                                {player.name} ({player.cards?.length || 0} cartes)
                              </option>
                            ))
                          : null
                        }
                      </select>
                      
                      <div className="swap-mode">
                        <label>
                          <input
                            type="radio"
                            name="swapMode"
                            value="single"
                            checked={swapMode === 'single'}
                            onChange={(e) => setSwapMode(e.target.value)}
                          />
                          1 carte
                        </label>
                        <label>
                          <input
                            type="radio"
                            name="swapMode"
                            value="all"
                            checked={swapMode === 'all'}
                            onChange={(e) => setSwapMode(e.target.value)}
                          />
                          Tout le paquet
                        </label>
                      </div>
                    </div>
                  )}

                  <button
                    onClick={playCard}
                    disabled={loading || 
                      ((selectedCard.type === 'letter' || selectedCard.type === 'joker') && !userWord.trim()) ||
                      (selectedCard.type === 'joker' && !jokerLetter) ||
                      (selectedCard.type === 'swap' && !swapTarget)
                    }
                    className="btn btn-primary play-btn"
                  >
                    {loading ? 'Jouer...' : 'Jouer la carte'}
                  </button>
                </div>
              )}
            </div>
          ) : (
            <div className="waiting-turn">
              <p>⏳ C'est au tour de <strong>{currentPlayer?.name}</strong></p>
            </div>
          )}
        </div>
      )}

      {/* Phase de vote */}
      {gamePhase === 'voting' && roomData.currentPlay && (
        <div className="voting-phase">
          <Timer
            duration={5}
            onTimeout={() => handleTimeout('voting')}
            startTime={roomData.votingStartTime}
            active={true}
          />

          <div className="current-play">
            <h2>🗳️ Vote en cours</h2>
            <div className="play-info">
              <p><strong>{roomData.currentPlay.playerName}</strong> a joué:</p>
              <div className="played-card">
                {roomData.currentPlay.card.type === 'letter' && (
                  <span className="letter">{roomData.currentPlay.card.letter}</span>
                )}
                {roomData.currentPlay.card.type === 'joker' && (
                  <span className="joker">🃏 → {roomData.currentPlay.jokerLetter}</span>
                )}
              </div>
              <p className="word-played">Mot: <strong>{roomData.currentPlay.word}</strong></p>
            </div>
          </div>

          {roomData.currentPlay.playerId !== playerId ? (
            <div className="vote-buttons">
              <button
                onClick={() => submitVote(true)}
                disabled={loading || vote !== null}
                className="btn btn-success vote-btn"
              >
                ✅ Valide
              </button>
              <button
                onClick={() => submitVote(false)}
                disabled={loading || vote !== null}
                className="btn btn-danger vote-btn"
              >
                ❌ Invalide
              </button>
              {vote !== null && (
                <p className="vote-submitted">
                  Votre vote: {vote ? '✅ Valide' : '❌ Invalide'}
                </p>
              )}
            </div>
          ) : (
            <div className="cannot-vote">
              <p>⏳ Vous ne pouvez pas voter sur votre propre réponse.</p>
            </div>
          )}

          <div className="vote-progress">
            <p>Votes reçus: {Object.keys(roomData.votes || {}).length}/{(roomData.players?.length || 1) - 1}</p>
          </div>
        </div>
      )}
    </div>
  );
}

function getNextPlayerIndex(currentIndex, direction, totalPlayers) {
  if (direction === 1) {
    return (currentIndex + 1) % totalPlayers;
  } else {
    return currentIndex === 0 ? totalPlayers - 1 : currentIndex - 1;
  }
}

export default Game;
