// src/components/Lobby.js

import React, { useEffect, useState } from 'react';
import { db } from '../firebase';
import { collection, doc, onSnapshot, updateDoc, setDoc } from 'firebase/firestore';
import { useNavigate } from 'react-router-dom';

function Lobby({ pseudo, gameCode, setGameCode }) {
  const [joueurs, setJoueurs] = useState([]);
  const [host, setHost] = useState(null);
  const [started, setStarted] = useState(false);
  const navigate = useNavigate();

  // Ajout automatique du joueur Ã  Firestore
  useEffect(() => {
    const playerRef = doc(db, "games", gameCode, "players", pseudo);
    setDoc(playerRef, {
      pseudo,
      cartes: [],
      out: false
    }, { merge: true });
  }, [gameCode, pseudo]);

  // Ã‰coute des joueurs
  useEffect(() => {
    const unsub = onSnapshot(collection(db, "games", gameCode, "players"), snapshot => {
      const list = snapshot.docs.map(doc => doc.data());
      setJoueurs(list);
    });
    return () => unsub();
  }, [gameCode]);

  // Ã‰coute du host et du dÃ©marrage
  useEffect(() => {
    const unsub = onSnapshot(doc(db, "games", gameCode), (docSnap) => {
      if (docSnap.exists()) {
        const data = docSnap.data();
        setHost(data.host);
        setStarted(data.started);
      }
    });
    return () => unsub();
  }, [gameCode]);

  // Redirection vers la partie
  useEffect(() => {
    if (started) {
      navigate(`/game/${gameCode}`);
    }
  }, [started, gameCode, navigate]);

  const handleStart = async () => {
    await updateDoc(doc(db, "games", gameCode), {
      started: true
    });
  };

  return (
    <div style={{ padding: 30 }}>
      <h2>ðŸ”— Code de partie : {gameCode}</h2>
      <p>ðŸ‘‘ Host : <strong>{host}</strong></p>

      <h3>ðŸ™‹ Joueurs dans la partie :</h3>
      <ul>
        {joueurs.map((j, i) => (
          <li key={i}>{j.pseudo}</li>
        ))}
      </ul>

      <p style={{ marginTop: 20, fontStyle: 'italic' }}>
        Pour jouer, soyez en vocal avec vos amis (Discord, Facetimeâ€¦) ou en prÃ©sentiel.
      </p>

      {pseudo === host && (
        <button onClick={handleStart} style={{ marginTop: 20, fontSize: 18, padding: '10px 20px' }}>
          ðŸš€ DÃ©marrer la partie
        </button>
      )}
    </div>
  );
}

export default Lobby;
