import React, { useEffect, useState } from 'react';
import { db } from '../firebase';
import {
  doc,
  getDoc,
  onSnapshot,
  updateDoc,
  arrayRemove,
  arrayUnion,
  setDoc,
  deleteDoc,
  collection,
} from 'firebase/firestore';

import VotePanel from './VotePanel';

function Game({ gameCode, pseudo }) {
  const [cartes, setCartes] = useState([]);
  const [joueurActif, setJoueurActif] = useState(null);
  const [piocheRestante, setPiocheRestante] = useState(0);
  const [timer, setTimer] = useState(15);
  const [monTour, setMonTour] = useState(false);
  const [votePhase, setVotePhase] = useState(false);
  const [playedCard, setPlayedCard] = useState('');

  useEffect(() => {
    const playerRef = doc(db, 'games', gameCode, 'players', pseudo);
    const unsubPlayer = onSnapshot(playerRef, (docSnap) => {
      if (docSnap.exists()) {
        setCartes(docSnap.data().cartes || []);
      }
    });

    const gameRef = doc(db, 'games', gameCode);
    const unsubGame = onSnapshot(gameRef, (docSnap) => {
      if (docSnap.exists()) {
        const data = docSnap.data();
        setJoueurActif(data.currentPlayer);
        setPiocheRestante(data.pioche?.length || 0);
        setMonTour(data.currentPlayer === pseudo);
        setTimer(15);
      }
    });

    return () => {
      unsubPlayer();
      unsubGame();
    };
  }, [gameCode, pseudo]);

  useEffect(() => {
    if (!monTour || votePhase) return;
    if (timer === 0) {
      handleTimeout();
      return;
    }
    const interval = setInterval(() => setTimer((prev) => prev - 1), 1000);
    return () => clearInterval(interval);
  }, [monTour, timer, votePhase]);

  const getNextPlayer = (gameData, current) => {
    const sens = gameData.sens || 1;
    const players = Object.keys(gameData.players || {});
    const index = players.indexOf(current);
    const nextIndex = (index + sens + players.length) % players.length;
    return players[nextIndex];
  };

  const handleTimeout = async () => {
    const gameRef = doc(db, 'games', gameCode);
    const snap = await getDoc(gameRef);
    const gameData = snap.data();
    const pioche = gameData.pioche || [];
    const nextCard = pioche[0];
    const newPioche = pioche.slice(1);

    await updateDoc(doc(db, 'games', gameCode, 'players', pseudo), {
      cartes: arrayUnion(nextCard),
    });

    await updateDoc(gameRef, {
      pioche: newPioche,
      currentPlayer: getNextPlayer(gameData, pseudo),
    });
  };

  const difficultyScore = (letter) => {
    const easy = 'AEIOTNRSLU';
    const medium = 'BCDFGHJKMP';
    const hard = 'QVWXYZ';
    if (easy.includes(letter)) return 0;
    if (medium.includes(letter)) return 1;
    if (hard.includes(letter)) return 2;
    return 3;
  };

  const startVote = async (card) => {
    setPlayedCard(card);
    setVotePhase(true);

    const voteDoc = doc(db, 'games', gameCode, 'votes', pseudo);
    await setDoc(voteDoc, { card, timestamp: Date.now() });

    setTimeout(() => countVotes(card), 5000);
  };

  const countVotes = async (card) => {
    const gameRef = doc(db, 'games', gameCode);
    const snap = await getDoc(gameRef);
    const gameData = snap.data();
    const voters = Object.keys(gameData.players || {}).filter((p) => p !== pseudo);

    let valides = 0;
    let refuses = 0;
    for (const voter of voters) {
      const voteSnap = await getDoc(doc(db, 'games', gameCode, 'votes', voter));
      if (voteSnap.exists()) {
        const vote = voteSnap.data();
        if (vote.choice === true) valides++;
        if (vote.choice === false) refuses++;
      }
    }

    const next = getNextPlayer(gameData, pseudo);
    const playerRef = doc(db, 'games', gameCode, 'players', pseudo);

    if (refuses >= valides) {
      const pioche = gameData.pioche || [];
      const bonus = pioche[0];
      const newPioche = pioche.slice(1);
      await updateDoc(playerRef, { cartes: arrayUnion(card, bonus) });
      await updateDoc(gameRef, { pioche: newPioche, currentPlayer: next });
    } else {
      await updateDoc(playerRef, { cartes: arrayRemove(card) });
      await updateDoc(gameRef, { currentPlayer: next });
    }

    for (const voter of voters) {
      await deleteDoc(doc(db, 'games', gameCode, 'votes', voter));
    }

    setVotePhase(false);
    setPlayedCard('');
  };

  const handleVote = async (choice) => {
    await setDoc(doc(db, 'games', gameCode, 'votes', pseudo), {
      choice,
      timestamp: Date.now(),
    });
  };

  const handlePlayCard = async (card) => {
    if (card.match(/^[A-Z]$/)) {
      await startVote(card);
    } else if (card.startsWith('joker')) {
      const l = prompt("Quelle lettre commence ton mot ?");
      if (!l || l.length !== 1) return alert("Lettre invalide.");
      const lettre = l.toUpperCase();
      const penalty = difficultyScore(lettre);

      const gameRef = doc(db, 'games', gameCode);
      const snap = await getDoc(gameRef);
      const gameData = snap.data();
      const pioche = gameData.pioche || [];
      const toGive = pioche.slice(0, penalty);
      const newPioche = pioche.slice(penalty);
      const next = getNextPlayer(gameData, pseudo);

      await updateDoc(doc(db, 'games', gameCode, 'players', pseudo), {
        cartes: arrayRemove(card),
      });

      await updateDoc(doc(db, 'games', gameCode, 'players', next), {
        cartes: arrayUnion(...toGive),
      });

      await updateDoc(gameRef, {
        pioche: newPioche,
        currentPlayer: next,
      });
    } else {
      const gameRef = doc(db, 'games', gameCode);
      const snap = await getDoc(gameRef);
      const gameData = snap.data();
      await updateDoc(doc(db, 'games', gameCode, 'players', pseudo), {
        cartes: arrayRemove(card),
      });
      await updateDoc(gameRef, {
        currentPlayer: getNextPlayer(gameData, pseudo),
      });
    }
  };

  return (
    <div style={{ padding: 20 }}>
      <h2>üéÆ Partie : {gameCode}</h2>
      <p>Tu es : <strong>{pseudo}</strong></p>
      <p>Joueur actif : <strong>{joueurActif}</strong></p>
      <p>Mon tour : {monTour ? "‚úÖ OUI" : "‚ùå NON"}</p>
      {monTour && !votePhase && <p>‚è≥ Temps restant : {timer}s</p>}

      <h3>üÉè Tes cartes :</h3>
      <div style={{ display: 'flex', flexWrap: 'wrap' }}>
        {cartes.map((carte, i) => (
          <button
            key={i}
            onClick={() => monTour && !votePhase && handlePlayCard(carte)}
            style={{
              margin: 5,
              padding: 10,
              backgroundColor: monTour ? '#d0f0c0' : '#eee',
              cursor: monTour ? 'pointer' : 'default',
            }}
          >
            {carte}
          </button>
        ))}
      </div>

      {!monTour && votePhase && <VotePanel onVote={handleVote} />}
    </div>
  );
}

export default Game;
