import React, { useEffect, useState } from 'react';
import { db } from '../firebase';
import {
  doc,
  getDoc,
  onSnapshot,
  updateDoc,
  arrayRemove,
  arrayUnion,
  setDoc,
  deleteDoc,
  collection
} from 'firebase/firestore';

import VotePanel from './VotePanel';
import ThemeSelector from './ThemeSelector';
import CurrentTheme from './CurrentTheme';
import WinnerPanel from './WinnerPanel';


function Game({ gameCode, pseudo }) {
  const [cartes, setCartes] = useState([]);
  const [joueurActif, setJoueurActif] = useState(null);
  const [piocheRestante, setPiocheRestante] = useState(0);
  const [timer, setTimer] = useState(15);
  const [monTour, setMonTour] = useState(false);
  const [votePhase, setVotePhase] = useState(false);
  const [playedCard, setPlayedCard] = useState('');
  const [themeActuel, setThemeActuel] = useState('');
  const [choisirTheme, setChoisirTheme] = useState(false);
  const [sens, setSens] = useState(1);
  const [joueurs, setJoueurs] = useState([]);
  const [fini, setFini] = useState(false);

  useEffect(() => {
    const unsubPlayers = onSnapshot(collection(db, "games", gameCode, "players"), snapshot => {
      const data = [];
      snapshot.forEach(doc => {
        const d = doc.data();
        data.push({ pseudo: doc.id, cartes: d.cartes || [], out: d.out });
        if (doc.id === pseudo) setCartes(d.cartes || []);
      });
      setJoueurs(data);
    });

    const unsubGame = onSnapshot(doc(db, "games", gameCode), (docSnap) => {
      if (docSnap.exists()) {
        const data = docSnap.data();
        setJoueurActif(data.currentPlayer);
        setMonTour(data.currentPlayer === pseudo);
        setPiocheRestante(data.pioche?.length || 0);
        setThemeActuel(data.theme || '');
        setSens(data.sens || 1);
        setTimer(15);
      }
    });

    return () => {
      unsubPlayers();
      unsubGame();
    };
  }, [gameCode, pseudo]);

  useEffect(() => {
    if (!monTour || votePhase || fini) return;
    if (timer === 0) {
      handleTimeout();
      return;
    }
    const interval = setInterval(() => setTimer((t) => t - 1), 1000);
    return () => clearInterval(interval);
  }, [monTour, timer, votePhase, fini]);

  const getNextPlayer = (current, joueursActifs) => {
    const list = joueursActifs.map(j => j.pseudo);
    const index = list.indexOf(current);
    const nextIndex = (index + sens + list.length) % list.length;
    return list[nextIndex];
  };

  const handleTimeout = async () => {
    const gameRef = doc(db, "games", gameCode);
    const snap = await getDoc(gameRef);
    const gameData = snap.data();
    const pioche = gameData.pioche || [];
    const nextCard = pioche[0];
    const newPioche = pioche.slice(1);
    const next = getNextPlayer(pseudo, joueurs.filter(j => !j.out));

    await updateDoc(doc(db, "games", gameCode, "players", pseudo), {
      cartes: arrayUnion(nextCard)
    });

    await updateDoc(gameRef, {
      pioche: newPioche,
      currentPlayer: next
    });
  };

  const difficultyScore = (letter) => {
    const easy = 'AEIOTNRSLU';
    const medium = 'BCDFGHJKMP';
    const hard = 'QVWXYZ';
    if (easy.includes(letter)) return 0;
    if (medium.includes(letter)) return 1;
    if (hard.includes(letter)) return 2;
    return 3;
  };

  const startVote = async (card) => {
    setPlayedCard(card);
    setVotePhase(true);
    setTimeout(() => countVotes(card), 5000);
  };

  const countVotes = async (card) => {
    const voters = joueurs.filter(j => j.pseudo !== pseudo && !j.out);
    let valides = 0;
    let refuses = 0;

    for (const voter of voters) {
      const voteSnap = await getDoc(doc(db, "games", gameCode, "votes", voter.pseudo));
      if (voteSnap.exists()) {
        const v = voteSnap.data();
        if (v.choice === true) valides++;
        if (v.choice === false) refuses++;
      }
    }

    const next = getNextPlayer(pseudo, joueurs.filter(j => !j.out));
    const playerRef = doc(db, "games", gameCode, "players", pseudo);
    const gameRef = doc(db, "games", gameCode);

    if (refuses >= valides) {
      const snap = await getDoc(gameRef);
      const pioche = snap.data().pioche || [];
      const bonus = pioche[0];
      const newPioche = pioche.slice(1);

      await updateDoc(playerRef, { cartes: arrayUnion(card, bonus) });
      await updateDoc(gameRef, { pioche: newPioche, currentPlayer: next });
    } else {
      await updateDoc(playerRef, { cartes: arrayRemove(card) });
      await updateDoc(gameRef, { currentPlayer: next });
    }

    for (const v of voters) {
      await deleteDoc(doc(db, "games", gameCode, "votes", v.pseudo));
    }

    checkVictory();
    setVotePhase(false);
    setPlayedCard('');
  };

  const handleVote = async (choice) => {
    await setDoc(doc(db, "games", gameCode, "votes", pseudo), {
      choice,
      timestamp: Date.now()
    });
  };
    const handlePlayCard = async (card) => {
    if (card.match(/^[A-Z]$/)) {
      await startVote(card);
    } else if (card.startsWith('joker')) {
      const l = prompt("Lettre de ton mot ?");
      if (!l || l.length !== 1) return alert("Lettre invalide.");
      const lettre = l.toUpperCase();
      const penalty = difficultyScore(lettre);
      const snap = await getDoc(doc(db, 'games', gameCode));
      const data = snap.data();
      const pioche = data.pioche || [];
      const toGive = pioche.slice(0, penalty);
      const newPioche = pioche.slice(penalty);
      const next = getNextPlayer(pseudo, joueurs.filter(j => !j.out));

      await updateDoc(doc(db, 'games', gameCode, 'players', pseudo), {
        cartes: arrayRemove(card)
      });

      if (toGive.length > 0) {
        await updateDoc(doc(db, 'games', gameCode, 'players', next), {
          cartes: arrayUnion(...toGive)
        });
      }

      await updateDoc(doc(db, 'games', gameCode), {
        pioche: newPioche,
        currentPlayer: next
      });
    } else if (card === 'pass') {
      const snap = await getDoc(doc(db, 'games', gameCode));
      const next = getNextPlayer(
        getNextPlayer(pseudo, joueurs.filter(j => !j.out)),
        joueurs.filter(j => !j.out)
      );
      await updateDoc(doc(db, 'games', gameCode, 'players', pseudo), {
        cartes: arrayRemove(card)
      });
      await updateDoc(doc(db, 'games', gameCode), {
        currentPlayer: next
      });
    } else if (card === 'reverse') {
      const newSens = -sens;
      const next = getNextPlayer(pseudo, joueurs.filter(j => !j.out));
      await updateDoc(doc(db, 'games', gameCode, 'players', pseudo), {
        cartes: arrayRemove(card)
      });
      await updateDoc(doc(db, 'games', gameCode), {
        sens: newSens,
        currentPlayer: next
      });
    } else if (card === 'crakitoko') {
      setChoisirTheme(true);
    }
  };

  const handleThemeChosen = async (theme) => {
    const next = getNextPlayer(pseudo, joueurs.filter(j => !j.out));
    await updateDoc(doc(db, "games", gameCode), {
      theme,
      currentPlayer: next
    });
    await updateDoc(doc(db, 'games', gameCode, 'players', pseudo), {
      cartes: arrayRemove('crakitoko')
    });
    setChoisirTheme(false);
  };

  const checkVictory = async () => {
    const playerRef = doc(db, "games", gameCode, "players", pseudo);
    if (cartes.length <= 1 && !fini) {
      await updateDoc(playerRef, { out: true });
      setFini(true);
    }
  };

const winners = joueurs.filter(j => j.out);
const partieTerminee = joueurs.length > 0 && winners.length === joueurs.length;

if (partieTerminee) {
  return <WinnerPanel winners={winners.map(w => w.pseudo)} />;
}

  return (
    <div style={{ padding: 20 }}>
      <h2>üéÆ Partie : {gameCode}</h2>
      <p>Tu es : <strong>{pseudo}</strong></p>
      <p>Joueur actif : <strong>{joueurActif}</strong></p>

      <CurrentTheme theme={themeActuel} />

      {monTour && !votePhase && <p>‚è≥ Temps : {timer}s</p>}
      <h3>üÉè Tes cartes :</h3>
      <div style={{ display: 'flex', flexWrap: 'wrap' }}>
        {cartes.map((carte, i) => (
          <button
            key={i}
            onClick={() => monTour && !votePhase && handlePlayCard(carte)}
            style={{
              margin: 5,
              padding: 10,
              backgroundColor: monTour ? '#d0f0c0' : '#eee',
              cursor: monTour ? 'pointer' : 'default'
            }}
          >
            {carte}
          </button>
        ))}
      </div>
      {!monTour && votePhase && <VotePanel onVote={handleVote} />}
      {monTour && choisirTheme && <ThemeSelector onSelect={handleThemeChosen} />}
    </div>
  );
}

export default Game;
