// src/components/Game.js

import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import { db } from '../firebase';
import {
  collection, doc, getDoc, onSnapshot, updateDoc, arrayUnion
} from 'firebase/firestore';
import VotePanel from './VotePanel';
import ThemeSelector from './ThemeSelector';
import CurrentTheme from './CurrentTheme';
import WinnerPanel from './WinnerPanel';

function Game({ pseudo }) {
  const { code: gameCode } = useParams();
  const [joueurs, setJoueurs] = useState([]);
  const [monPaquet, setMonPaquet] = useState([]);
  const [monTour, setMonTour] = useState(false);
  const [sens, setSens] = useState(1); // 1 = horaire
  const [joueurActif, setJoueurActif] = useState('');
  const [themeActuel, setThemeActuel] = useState(null);
  const [votePhase, setVotePhase] = useState(false);
  const [timer, setTimer] = useState(15);
  const [resultatVote, setResultatVote] = useState(null);
  const [showThemeSelector, setShowThemeSelector] = useState(false);

  const cartesInitiales = [
    'A', 'B', 'C', 'D', 'E', 'F', 'G',
    'JOKER', 'PASS', 'REVERSE', 'CRAKITOKO'
  ];

  // Donne 8 cartes al√©atoires au joueur au d√©but
  useEffect(() => {
    const distribuerCartes = async () => {
      const joueurRef = doc(db, "games", gameCode, "players", pseudo);
      const snap = await getDoc(joueurRef);
      const data = snap.data();

      if (!data || data.cartes.length === 0) {
        const paquet = Array.from({ length: 8 }, () =>
          cartesInitiales[Math.floor(Math.random() * cartesInitiales.length)]
        );
        await updateDoc(joueurRef, { cartes: paquet });
        setMonPaquet(paquet);
      } else {
        setMonPaquet(data.cartes);
      }
    };
    distribuerCartes();
  }, [gameCode, pseudo]);

  // √âcoute des joueurs et de l‚Äôordre
  useEffect(() => {
    const unsub = onSnapshot(collection(db, "games", gameCode, "players"), (snap) => {
      const list = snap.docs.map(d => ({ id: d.id, ...d.data() }));
      setJoueurs(list);

      const noms = list.map(j => j.pseudo);
      const indexActuel = noms.indexOf(joueurActif);
      const monIndex = noms.indexOf(pseudo);

      // D√©tection de mon tour
      if (joueurActif && noms.length > 1) {
        setMonTour(noms[indexActuel] === noms[monIndex]);
      }
    });

    return () => unsub();
  }, [gameCode, joueurActif, pseudo]);

  // Timer 15 secondes
  useEffect(() => {
    if (!monTour || votePhase) return;
    setTimer(15);
    const interval = setInterval(() => {
      setTimer(t => {
        if (t <= 1) {
          handleTempsEcoule();
          clearInterval(interval);
        }
        return t - 1;
      });
    }, 1000);
    return () => clearInterval(interval);
  }, [monTour, votePhase]);

  const handleTempsEcoule = () => {
    const joueurRef = doc(db, "games", gameCode, "players", pseudo);
    updateDoc(joueurRef, {
      cartes: arrayUnion(cartesInitiales[Math.floor(Math.random() * cartesInitiales.length)])
    });
    passerAuSuivant();
  };

  const passerAuSuivant = () => {
    const noms = joueurs.map(j => j.pseudo);
    const index = noms.indexOf(joueurActif);
    let nextIndex = (index + sens + noms.length) % noms.length;
    setJoueurActif(noms[nextIndex]);
  };

  const jouerCarte = async (carte) => {
    const joueurRef = doc(db, "games", gameCode, "players", pseudo);
    const nouvellesCartes = monPaquet.filter(c => c !== carte);
    await updateDoc(joueurRef, { cartes: nouvellesCartes });
    setMonPaquet(nouvellesCartes);

    if (carte === 'PASS') {
      passerAuSuivant(); // saute un joueur
      passerAuSuivant();
    } else if (carte === 'REVERSE') {
      setSens(s => -s);
      passerAuSuivant();
    } else if (carte === 'CRAKITOKO') {
      setShowThemeSelector(true);
    } else if (carte === 'JOKER') {
      setVotePhase(true);
    } else {
      setVotePhase(true);
    }

    if (nouvellesCartes.length === 0) {
      await updateDoc(joueurRef, { out: true });
    }
  };

  const handleVoteResult = (resultat) => {
    setVotePhase(false);
    setResultatVote(resultat);

    if (resultat === 'valid√©') {
      passerAuSuivant();
    } else {
      const joueurRef = doc(db, "games", gameCode, "players", pseudo);
      const nouvelle = cartesInitiales[Math.floor(Math.random() * cartesInitiales.length)];
      updateDoc(joueurRef, {
        cartes: arrayUnion(nouvelle)
      });
    }
  };

  const handleThemeSelection = (theme) => {
    setThemeActuel(theme);
    setShowThemeSelector(false);
    passerAuSuivant();
  };

  const winners = joueurs.filter(j => j.out);
  const partieTerminee = joueurs.length > 0 && winners.length === joueurs.length;

  if (partieTerminee) {
    return <WinnerPanel winners={winners.map(w => w.pseudo)} />;
  }
  return (
    <div style={{ padding: 20 }}>
      <h2>üéÆ Partie : {gameCode}</h2>
      <p>Tu es : <strong>{pseudo}</strong></p>
      <p>Joueur actif : <strong>{joueurActif}</strong></p>

      <CurrentTheme theme={themeActuel} />

      {monTour && !votePhase && <p>‚è≥ Temps : {timer}s</p>}
      {resultatVote && <p>R√©sultat du vote pr√©c√©dent : <strong>{resultatVote}</strong></p>}

      <h3>üÉè Tes cartes :</h3>
      <div style={{ display: 'flex', flexWrap: 'wrap', gap: 10 }}>
        {monTour && !votePhase ? (
          monPaquet.map((carte, i) => (
            <button key={i} onClick={() => jouerCarte(carte)}>
              {carte}
            </button>
          ))
        ) : (
          monPaquet.map((carte, i) => (
            <span key={i} style={{ padding: '6px 12px', backgroundColor: '#eee', borderRadius: 4 }}>
              {carte}
            </span>
          ))
        )}
      </div>

      {showThemeSelector && (
        <ThemeSelector onSelect={handleThemeSelection} />
      )}

      {votePhase && !showThemeSelector && (
        <VotePanel pseudoJoueurActif={joueurActif} onVoteResult={handleVoteResult} isMyTurn={monTour} />
      )}
    </div>
  );
}

export default Game;
