// src/App.js
import React, { useState } from 'react';

function App() {
  const [pseudo, setPseudo] = useState('');
  const [gameMode, setGameMode] = useState(null); // 'create' ou 'join'
  const [gameCode, setGameCode] = useState('');

  const handleStart = () => {
    if (!pseudo.trim()) {
      alert("Entre un pseudo !");
      return;
    }

    if (gameMode === 'create') {
      alert(`CrÃ©ation de partie pour ${pseudo} (Ã  connecter Ã  Firebase plus tard)`);
    } else if (gameMode === 'join') {
      if (!gameCode.trim()) {
        alert("Entre un code de partie !");
        return;
      }
      alert(`Tentative de rejoindre ${gameCode} en tant que ${pseudo}`);
    }
  };

  return (
    <div style={{ padding: 20, fontFamily: 'sans-serif' }}>
      <h1>ðŸŽ´ Crack List Web</h1>
      <label>
        Ton pseudo :
        <input
          type="text"
          value={pseudo}
          onChange={(e) => setPseudo(e.target.value)}
          placeholder="Ex: lamtm"
        />
      </label>

      <div style={{ marginTop: 20 }}>
        <button onClick={() => setGameMode('create')}>CrÃ©er une partie</button>
        <button onClick={() => setGameMode('join')}>Rejoindre une partie</button>
      </div>

      {gameMode === 'join' && (
        <div style={{ marginTop: 20 }}>
          <label>
            Code de partie :
            <input
              type="text"
              value={gameCode}
              onChange={(e) => setGameCode(e.target.value)}
              placeholder="Ex: ABC123"
            />
          </label>
        </div>
      )}

      {gameMode && (
        <div style={{ marginTop: 30 }}>
          <button onClick={handleStart}>C'est parti</button>
        </div>
      )}
    </div>
  );
}

export default App;
