// src/components/Game.js
import React, { useEffect, useState } from 'react';
import { db } from '../firebase';
import {
  doc,
  getDoc,
  onSnapshot,
  collection
} from 'firebase/firestore';

function Game({ gameCode, pseudo }) {
  const [cartes, setCartes] = useState([]);
  const [joueurActif, setJoueurActif] = useState(null);
  const [piocheRestante, setPiocheRestante] = useState(0);

  useEffect(() => {
    // Ã‰couter les cartes du joueur
    const playerRef = doc(db, "games", gameCode, "players", pseudo);
    const unsubPlayer = onSnapshot(playerRef, (docSnap) => {
      if (docSnap.exists()) {
        const data = docSnap.data();
        if (data.cartes) setCartes(data.cartes);
      }
    });

    // Ã‰couter les infos gÃ©nÃ©rales de la partie
    const gameRef = doc(db, "games", gameCode);
    const unsubGame = onSnapshot(gameRef, (docSnap) => {
      if (docSnap.exists()) {
        const data = docSnap.data();
        setJoueurActif(data.currentPlayer);
        setPiocheRestante(data.pioche?.length || 0);
      }
    });

    return () => {
      unsubPlayer();
      unsubGame();
    };
  }, [gameCode, pseudo]);

  return (
    <div style={{ padding: 20 }}>
      <h2>ðŸŽ® Partie en cours : {gameCode}</h2>
      <p>Tu es : <strong>{pseudo}</strong></p>
      <p>Joueur actif : <strong>{joueurActif}</strong></p>
      <p>Cartes en main : {cartes.length}</p>
      <p>Cartes restantes dans la pioche : {piocheRestante}</p>

      <div style={{ marginTop: 20 }}>
        {cartes.map((carte, i) => (
          <button key={i} style={{ margin: 5 }}>
            {carte}
          </button>
        ))}
      </div>
    </div>
  );
}

export default Game;
