// src/components/Lobby.js
import React, { useEffect, useState } from 'react';
import { db } from '../firebase';
import { doc, onSnapshot, collection, addDoc, updateDoc } from 'firebase/firestore';

function Lobby({ gameCode, pseudo, isHost }) {
  const [joueurs, setJoueurs] = useState([]);

  useEffect(() => {
    const unsubscribe = onSnapshot(collection(db, "games", gameCode, "players"), (snapshot) => {
      const data = snapshot.docs.map(doc => doc.data());
      setJoueurs(data);
    });
    return () => unsubscribe();
  }, [gameCode]);

  const handleStart = async () => {
    const gameRef = doc(db, "games", gameCode);
    await updateDoc(gameRef, { started: true });
  };

  return (
    <div style={{ padding: 20 }}>
      <h2>üîó Code de partie : <span style={{ fontFamily: 'monospace' }}>{gameCode}</span></h2>

      <p>üëë <strong>Host</strong> : {isHost ? pseudo : ''}</p>

      <p>üó£Ô∏è <strong>Joueurs dans la partie :</strong></p>
      <ul>
        {joueurs.map((j, i) => (
          <li key={i}>{j.pseudo}</li>
        ))}
      </ul>

      <p style={{ fontStyle: 'italic', marginTop: 20 }}>
        Pour jouer, soyez en vocal avec vos amis (Discord, Facetime‚Ä¶) ou en pr√©sentiel.
      </p>

      {isHost && (
        <div style={{ marginTop: 30 }}>
          <button onClick={handleStart} style={{ padding: '10px 20px', fontSize: '1rem' }}>
            üöÄ D√©marrer la partie
          </button>
        </div>
      )}
    </div>
  );
}

export default Lobby;
