import React, { useState, useEffect } from 'react';
import { doc, updateDoc } from 'firebase/firestore';
import { db } from '../firebase';

function Lobby({ roomCode, playerName, playerId, roomData, onLeaveRoom }) {
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  const isHost = roomData?.players && Array.isArray(roomData.players) 
    ? roomData.players.find(p => p.id === playerId)?.isHost || false 
    : false;
  const canStart = roomData?.players && Array.isArray(roomData.players) 
    ? roomData.players.length >= 2 
    : false;

  useEffect(() => {
    // Mettre √† jour la pr√©sence du joueur
    const updatePresence = async () => {
      if (roomData && roomData.players && Array.isArray(roomData.players)) {
        const playerIndex = roomData.players.findIndex(p => p.id === playerId);
        if (playerIndex !== -1) {
          await updateDoc(doc(db, 'rooms', roomCode), {
            [`players.${playerIndex}.lastSeen`]: Date.now(),
            [`players.${playerIndex}.connected`]: true
          });
        }
      }
    };

    updatePresence();
    const interval = setInterval(updatePresence, 10000); // Toutes les 10 secondes

    return () => clearInterval(interval);
  }, [roomCode, playerId, roomData]);

  const startGame = async () => {
    if (!canStart) {
      setError('Il faut au moins 2 joueurs pour commencer');
      return;
    }

    setLoading(true);
    setError('');

    try {
      // Choisir un joueur al√©atoire pour choisir le th√®me initial
      const randomPlayerIndex = Math.floor(Math.random() * roomData.players.length);
      
      await updateDoc(doc(db, 'rooms', roomCode), {
        gameStarted: true,
        currentPlayerIndex: randomPlayerIndex,
        themeSelectionPhase: true,
        gamePhase: 'theme-selection',
        currentRound: 1,
        gameStartTime: Date.now()
      });
    } catch (err) {
      setError('Erreur lors du d√©marrage de la partie');
      console.error(err);
    }

    setLoading(false);
  };

  const leaveRoom = async () => {
    try {
      if (roomData && roomData.players && Array.isArray(roomData.players)) {
        const updatedPlayers = roomData.players.filter(p => p.id !== playerId);
        
        if (updatedPlayers.length === 0) {
          // Supprimer la room si plus de joueurs
          // Note: Vous pourriez vouloir impl√©menter une fonction cloud pour nettoyer automatiquement
        } else {
          // Si l'h√¥te part, donner les droits d'h√¥te au premier joueur restant
          if (isHost && updatedPlayers.length > 0) {
            updatedPlayers[0].isHost = true;
          }
          
          await updateDoc(doc(db, 'rooms', roomCode), {
            players: updatedPlayers
          });
        }
      }
    } catch (err) {
      console.error('Erreur lors de la sortie de la room:', err);
    }
    
    onLeaveRoom();
  };

  if (!roomData) {
    return <div className="loading">Chargement...</div>;
  }

  return (
    <div className="lobby">
      <div className="lobby-container">
        <div className="lobby-header">
          <h1>üéÆ Salle: {roomCode}</h1>
          <button onClick={leaveRoom} className="btn btn-danger leave-btn">
            Quitter
          </button>
        </div>

        <div className="players-section">
          <h2>üë• Joueurs ({roomData.players?.length || 0}/10)</h2>
          <div className="players-list">
            {roomData.players && Array.isArray(roomData.players) ? roomData.players.map((player, index) => (
              <div key={player.id} className={`player-card ${!player.connected ? 'disconnected' : ''}`}>
                <span className="player-name">
                  {player.name}
                  {player.isHost && <span className="host-badge">üëë</span>}
                  {!player.connected && <span className="disconnect-badge">üì¥</span>}
                </span>
                <span className="cards-count">{player.cards?.length || 0} cartes</span>
              </div>
            )) : (
              <div>Chargement des joueurs...</div>
            )}
          </div>
        </div>

        {isHost && (
          <div className="host-controls">
            <h3>üéØ Contr√¥les d'h√¥te</h3>
            
            {!canStart && (
              <p className="warning">Il faut au moins 2 joueurs pour commencer la partie.</p>
            )}
            
            <button 
              onClick={startGame}
              disabled={!canStart || loading}
              className="btn btn-primary start-btn"
            >
              {loading ? 'D√©marrage...' : 'Commencer la partie'}
            </button>
            
            {error && <div className="error-message">{error}</div>}
          </div>
        )}

        {!isHost && (
          <div className="waiting-message">
            <p>‚è≥ En attente que l'h√¥te lance la partie...</p>
          </div>
        )}

        <div className="lobby-info">
          <h3>üìã Informations</h3>
          <ul>
            <li>üéØ Chaque joueur commence avec 8 cartes</li>
            <li>‚è±Ô∏è 15 secondes pour jouer, 5 secondes pour voter</li>
            <li>üÉè Utilisez les cartes sp√©ciales √† bon escient</li>
            <li>üèÜ Le dernier joueur avec des cartes gagne !</li>
          </ul>
        </div>
      </div>
    </div>
  );
}

export default Lobby;
