import React from 'react';

const THEMES = [
  ["Animaux marins", "Objets de la cuisine", "Choses rouges"],
  ["Pays d'Europe", "Plantes toxiques", "Inventions du XXe siècle"],
  ["Célébrités françaises", "Prénoms qui finissent en 'a'", "Sport d'hiver"]
];

function ThemeSelector({ onSelect }) {
  const options = THEMES[Math.floor(Math.random() * THEMES.length)];

  return (
    <div style={{ marginTop: 20 }}>
      <h3>Choisis un thème :</h3>
      {options.map((theme, index) => (
        <button
          key={index}
          onClick={() => onSelect(theme)}
          style={{
            display: 'block',
            margin: '10px auto',
            padding: '10px 20px',
            fontSize: '16px'
          }}
        >
          {theme}
        </button>
      ))}
    </div>
  );
}

export default ThemeSelector;
