import React from 'react';

function WinnerPanel({ winner, playersWithoutCards, onLeaveRoom }) {
  const winners = playersWithoutCards || (winner ? [winner] : []);
  const isMultipleWinners = winners.length > 1;
  const isFirstWinner = winner && winners.length === 1; // Premier √† finir

  return (
    <div className="winner-panel">
      <div className={`winner-container ${isFirstWinner ? 'first-winner' : ''}`}>
        {isFirstWinner && <div className="fireworks">üéÜüéá‚ú®</div>}
        <div className="confetti">üéâ</div>
        <h1 className="winner-title">üèÜ {isMultipleWinners ? 'Victoires !' : 'Victoire !'}</h1>
        
        {isMultipleWinners ? (
          <div className="multiple-winners">
            <p className="winner-message">F√©licitations aux gagnants !</p>
            <div className="winners-list">
              {winners.map((player, index) => (
                <div key={player.id} className="winner-name">
                  ü•á {player.name}
                </div>
              ))}
            </div>
            <p className="winner-subtitle">
              Vous avez tous r√©ussi √† vous d√©barrasser de vos cartes !
            </p>
          </div>
        ) : winner ? (
          <div className="single-winner">
            <div className="winner-name">{winner.name}</div>
            <p className="winner-message">
              F√©licitations ! Vous avez gagn√© en vous d√©barrassant de toutes vos cartes en premier !
            </p>
          </div>
        ) : (
          <div className="game-ended">
            <p className="winner-message">Partie termin√©e !</p>
          </div>
        )}
        
        <div className="winner-stats">
          <p>üÉè Objectif: Se d√©barrasser de toutes ses cartes</p>
          <p>‚è±Ô∏è Fin de partie: {new Date().toLocaleTimeString()}</p>
        </div>
        
        <button 
          onClick={onLeaveRoom}
          className="btn btn-primary"
        >
          Retour √† l'accueil
        </button>
      </div>
      
      <div className="celebration-background">
        <div className="confetti-piece">üéä</div>
        <div className="confetti-piece">‚ú®</div>
        <div className="confetti-piece">üéâ</div>
        <div className="confetti-piece">üåü</div>
        <div className="confetti-piece">üéä</div>
